;(function(){
  if (!window.AFRAME) {
    console.error('AFRAME not loaded');
    return;
  }
  AFRAME.registerComponent('joystick-controls', {
    schema: { mode: {default: 'movement'} },
    init: function(){
      console.log('joystick-controls initialized with mode', this.data.mode);
    }
  });
})();
